import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ReactPlayer from 'react-player';
import Moment from 'react-moment';

function Blog() {
    const [blogsData, setBlogsData] = useState({});
    const [contentData, setContentData] = useState([]);
    const [authorData, setAuthorData] = useState({});
    const [videoFile, setVideoFile] = useState(null);
    const [photoFile, setPhotoFile] = useState(null);
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [remarks, setRemarks] = useState('');
    
    let { blogs_id } = useParams();

    useEffect(() => {
        const fetchData = async () => {
            try {
                const res = await axios.get(`/blogs/${blogs_id}/`);
                setBlogsData(res.data);
                setContentData(res.data.blogs_content);
                setAuthorData(res.data.author);
            } catch (error) {
                console.error(error);
            }
        };

        fetchData();
    }, [blogs_id]);

    useEffect(() => {
        document.title = `${blogsData.title}`;
    }, [blogsData.title]);

    const handleVideoChange = (e) => {
        setVideoFile(e.target.files[0]);
    };

    const handlePhotoChange = (e) => {
        setPhotoFile(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('title', title);
        if (videoFile) {
            formData.append('video', videoFile);
        }
        if (photoFile) {
            formData.append('photo', photoFile);
        }
        formData.append('content', content);
        formData.append('remarks', remarks);

        try {
            const response = await axios.post(`/blogs/${blogs_id}/content/`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log(response.data);
            // Optionally, refresh content data after upload
            setContentData([...contentData, response.data]);
        } catch (error) {
            console.error('Error uploading content:', error);
        }
    };

    return (
        <div className="content-page">
            <div className="row">
                <div>
                    <h1>{blogsData.title}</h1>
                    <div>
                        <p className="lead">
                            <i className="fa fa-user"></i> by <a href="#/">{authorData.full_name}</a>
                        </p>
                    </div>
                    <hr />
                    <p>
                        <i className="fa fa-calendar"></i> Posted on: <Moment format="DD-MMM-YYYY h:mm A">{blogsData.updated_on}</Moment>
                    </p>
                    <br />
                    <p>
                        <i className="fa fa-tags"></i> Tags: <a href="#/"><span className="badge badge-info"></span></a>
                    </p>

                    {contentData.map((content, index) => (
                        <div key={index}>
                            <br />
                            <h2><center><strong>{content.title}</strong></center></h2>
                            <br /><br />

                            {content.video ? (
                                <div className="content-vid">
                                    <ReactPlayer url={content.video} controls={true} muted={true} playing={false} width='100%' height='100%' />
                                </div>
                            ) : (
                                <div className="content-img">
                                    <img src={content.photo} alt={content.title} width='100%' height='auto' />
                                </div>
                            )}

                            <br />
                            <p>{content.content}</p>
                            <br />
                            <h4>I like the post? Like this!</h4>
                        </div>
                    ))}

                    <form onSubmit={handleSubmit}>
                        <h3>Add New Content</h3>
                        <input type="text" placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
                        <br />
                        <input type="file" accept="video/*" onChange={handleVideoChange} />
                        <br />
                        <input type="file" accept="image/*" onChange={handlePhotoChange} />
                        <br />
                        <textarea placeholder="Content" value={content} onChange={(e) => setContent(e.target.value)} required />
                        <br />
                        <input type="text" placeholder="Remarks" value={remarks} onChange={(e) => setRemarks(e.target.value)} required />
                        <br />
                        <button type="submit">Upload Content</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Blog;
