import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Blogs from './Blogs/Blogs';

function Home() {
  const [userData, setUserData] = useState(null);
  const [isFirstLogin, setIsFirstLogin] = useState(true);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    document.title = 'Home Page';

    const fetchUser  = async () => {
      try {
        const res = await axios.get('/user/'); // Adjust the endpoint as necessary
        setUserData(res.data);

        // Check if the user has logged in before
        const hasLoggedInBefore = localStorage.getItem('hasLoggedInBefore');
        if (hasLoggedInBefore) {
          setIsFirstLogin(false);
        } else {
          localStorage.setItem('hasLoggedInBefore', 'true');
        }
      } catch (error) {
        console.log(error);
        setError('Failed to fetch user data.'); // Set error message
      } finally {
        setLoading(false); // Set loading to false after fetching
      }
    };

    fetchUser ();
  }, []);

  return (
    <div className="container">
      <div className="defrate-container">
        {/* First Defrate Section */}
        <section className="defrate-section" id="defrate-section-one">
          <div className='home-content'>
            {loading ? ( // Show loading state
              <h3>Loading...</h3>
            ) : error ? ( // Show error message if there is an error
              <h3>{error}</h3>
            ) : userData ? (
              <h3>
                {isFirstLogin ? 'Welcome' : 'Welcome Back'}, {`${userData.first_name} ${userData.last_name}`}
              </h3>
            ) : (
              <h3>User data not available</h3> // Fallback if userData is null
            )}
            <h1><span>Adil Iqbal</span></h1>
            <h3>& I Am a <span>Bloger</span></h3>
          </div>
        </section>

        {/* Second Defrate Section */}
        <section className="defrate-section" id="defrate-section-two">
          <Blogs />
        </section>
      </div>
    </div>
  );
}

export default Home;
