import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import ReactPlayer from 'react-player';
import Moment from 'react-moment';

function Content() {
  const [blogsData, setBlogsData] = useState([]);
  const [ContentData, setContentData] = useState([]);
  const [authorData, setAuthorData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likesCount, setLikesCount] = useState(0);
  const [commentText, setCommentText] = useState('');
  const [comments, setComments] = useState([]);
  const userId = 1; // Replace with the actual user ID

  let { blogs_id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/blogs/${blogs_id}`);
        setBlogsData(res.data);
        setContentData(res.data.blogs_content);
        setAuthorData(res.data.author);
      } catch (error) {
        setError('Error fetching data. Please try again later.');
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [blogs_id]);

  useEffect(() => {
    document.title = `${blogsData.title}`;
  }, [blogsData.title]);

  const handleLike = async () => {
    try {
      await axios.post('/likes/', { user: userId, content: content.id });
      setLikesCount(likesCount + 1);
    } catch (error) {
      console.error('Error liking the content', error);
    }
  };

  const fetchComments = async () => {
    const res = await axios.get(`/comments/${content.id}/`);
    setComments(res.data);
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`/comments/${content.id}/`, { user: userId, text: commentText });
      setCommentText('');
      fetchComments(); // Refresh comments
    } catch (error) {
      console.error('Error submitting comment', error);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [content.id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="content-page">
      <div className="row">
        <div>
          {ContentData.map((content, index) => (
            <div key={index}>
              <br />
              <h1><center><strong>{content.title}</strong></center></h1>
              <br /><br />

              {content.video ? (
                <div className="content-vid">
                  <ReactPlayer url={content.video} controls={true} muted={true} playing={false} width='100%' height='100%' />
                </div>
              ) : (
                <div className="content-img">
                  <img src={content.photo} alt={content.title} width='100%' height='100%' />
                </div>
              )}
              <br />
              <div>
                <p className="lead">
                  <i className="fa fa-user"></i> by <a href="#/">{authorData.full_name}</a>
                </p>
              </div>
              <hr />
              <p>
                <i className="fa fa-calendar"></i> Posted on: <Moment format="DD-MMM-YYYY h:mm A ">{blogsData.updated_on}</Moment>
              </p>
              <br />
              <p>
                <i className="fa fa-tags"></i> Tags: <a href="#/"><span className="badge badge-info"></span></a>
              </p>

              <p>{content.content}</p>
              <br />
              <h4>I like the post? Like this!</h4>
              <button onClick={handleLike}>Like ({likesCount})</button>

              <div>
                <h4>Comments:</h4>
                {comments.map(comment => (
                  <div key={comment.id}>
                    <p>{comment.text}</p>
                    <p><small>By: {comment.user.username}</small></p>
                  </div>
                ))}
                <form onSubmit={handleCommentSubmit}>
                  <textarea value={commentText} onChange={(e) => setCommentText(e.target.value)} />
                  <button type="submit">Submit Comment</button>
                </form>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Content;
