import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import ReactPlayer from 'react-player';
import Moment from 'react-moment';

function Content() {
  const [blogsData, setBlogsData] = useState([]);
  const [ContentData, setContentData] = useState([]);
  const [authorData, setAuthorData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [likesCount, setLikesCount] = useState(0);
  const [liked, setLiked] = useState(false); // Track if the user has liked the content
  const [commentText, setCommentText] = useState('');
  const [comments, setComments] = useState([]);
  const userId = userId; // Replace with the actual user ID or authentication check
  const isLoggedIn = userId !== null; // Example check, adjust based on your auth logic
  const navigate = useNavigate();

  let { blogs_id } = useParams();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/blogs/${blogs_id}`);
        setBlogsData(res.data);
        setContentData(res.data.blogs_content);
        setAuthorData(res.data.author);
      } catch (error) {
        setError('Error fetching data. Please try again later.');
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [blogs_id]);

  useEffect(() => {
    document.title = `${blogsData.title}`;
  }, [blogsData.title]);

  const handleLike = async (contentId) => {
    if (!isLoggedIn) {
      navigate('/login'); // Redirect to login page if not logged in
      return;
    }

    try {
      await axios.post('/likes/', { user: userId, content: contentId });
      setLikesCount(likesCount + 1);
      setLiked(true); // Set liked to true when the user successfully likes the content
    } catch (error) {
      if (error.response && error.response.status === 400) {
        console.error('You have already liked this content.');
      } else {
        console.error('Error liking the content', error);
      }
    }
  };

  const fetchComments = async (contentId) => {
    const res = await axios.get(`/comments/${contentId}/`);
    setComments(res.data);
  };

  const handleCommentSubmit = async (e, contentId) => {
    e.preventDefault();
    if (!isLoggedIn) {
      navigate('/login'); // Redirect to login page if not logged in
      return;
    }

    try {
      await axios.post(`/comments/${contentId}/`, { user: userId, text: commentText });
      setCommentText('');
      fetchComments(contentId); // Refresh comments
    } catch (error) {
      console.error('Error submitting comment', error);
    }
  };

  useEffect(() => {
    if (ContentData.length > 0) {
      fetchComments(ContentData[0].id); // Fetch comments for the first content item
    }
  }, [ContentData]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="content-page">
      <div className="row">
        <div>
          {ContentData.map((content, index) => (
            <div key={index} className="content-item">
              <h1 className="content-title">{content.title}</h1>
              {content.video ? (
                <div className="content-vid">
                  <ReactPlayer url={content.video} controls={true} muted={true} width='100%' height='100%' />
                </div>
              ) : (
                <div className="content-img">
                  <img src={content.photo} alt={content.title} className="img-responsive" />
                </div>
              )}
              <div className="content-meta">
                <p className="lead">
                  <i className="fa fa-user"></i> by <a href="#/">{authorData.full_name}</a>
                </p>
                <p>
                  <i className="fa fa-calendar"></i> Posted on: <Moment format="DD-MMM-YYYY h:mm A ">{blogsData.updated_on}</Moment>
                </p>
                <p>
                  <i className="fa fa-tags"></i> Tags: <a href="#/"><span className="badge badge-info"></span></a>
                </p>
              </div>
              <p>{content.content}</p>
              <br />
              <h4>I like the post? Like this!</h4>
              <button
                className={`btn ${liked ? 'btn-danger' : 'btn-primary'}`}
                onClick={() => handleLike(content.id)}
              >
                {liked ? <i className="fa fa-heart" style={{ color: 'red' }} /> : <i className="fa fa-heart-o" />}
                Like ({likesCount})
              </button>

              <div className="comments-section">
                <h4>Comments:</h4>
                {comments.map(comment => (
                  <div key={comment.id} className="comment">
                    <h5>{comment.text}</h5>
                    <h5><small>By: {comment.user.first_name}</small></h5>
                  </div>
                ))}
                <form onSubmit={(e) => handleCommentSubmit(e, content.id)}>
                  <textarea value={commentText} onChange={(e) => setCommentText(e.target.value)} className="form-control" />
                  <button type="submit" className="btn btn-primary">Submit Comment</button>
                </form>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Content;
